// Heading.js
import React, { useState } from 'react';
import { View, Text, TouchableOpacity, Vibration, StyleSheet } from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome';
import { useNavigation } from '@react-navigation/native';
export default function Heading() {
  const [showMenuOptions, setShowMenuOptions] = useState(false);

  const toggleMenuOptions = () => {
    setShowMenuOptions(!showMenuOptions);
  };

  return (
    <View style={styles.container}>
      <TouchableOpacity style={styles.menuIcon} onPress={toggleMenuOptions}>
        <Text style={styles.menuText}>â˜°</Text>
      </TouchableOpacity>

      {showMenuOptions && (
        <View style={[styles.menuOptions, { zIndex: 1 }]}>
          {/* Menu options */}
          <TouchableOpacity style={styles.menuOption} onPress={()=>navigateToAnotherPage("IncidentReportFrom")}>
            <Text style={styles.menuOptionText}>Create Experiment</Text>
          </TouchableOpacity>
          <TouchableOpacity style={styles.menuOption}>
            <Text style={styles.menuOptionText}>Create Tip</Text>
          </TouchableOpacity>
          <TouchableOpacity style={styles.menuOption}>
            <Text style={styles.menuOptionText}>View Hotspots</Text>
          </TouchableOpacity>
          <TouchableOpacity style={styles.menuOption}>
            <Text style={styles.menuOptionText}>View Tips</Text>
          </TouchableOpacity>
          <TouchableOpacity style={styles.menuOption}>
            <Text style={styles.menuOptionText}>Start a Trip</Text>
          </TouchableOpacity>
          <TouchableOpacity style={styles.menuOption}>
            <View style={styles.logoutContainer}>
              <Text style={styles.menuOptionText}>Logout</Text>
              <Icon name="sign-out" size={20} color="black" style={styles.logoutIcon} />
            </View>
          </TouchableOpacity>
        </View>
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    position: 'relative',
    backgroundColor: 'white',
  },
  menuIcon: {
    position: 'absolute',
    top: 10,
    right: 10,
    padding: 10,
  },
  menuText: {
    color: 'black',
    fontSize: 24,
  },
  menuOptions: {
    backgroundColor: 'lightgrey',
    position: 'absolute',
    top: 50,
    right: 10,
    width: 150,
    borderRadius: 5,
    borderWidth: 1,
    borderColor: 'gray',
  },
  menuOption: {
    padding: 10,
  },
  menuOptionText: {
    color: 'black',
    fontSize: 14,
  },
  logoutContainer: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  logoutIcon: {
    marginLeft: 10,
  },
});
